// DO NOT CHANGE NO MATTER WHAT: Function to get formatted timestamp
const getFormattedTimestamp = () => {
    const now = new Date();
    const optionsDate = { year: 'numeric', month: 'short', day: '2-digit', timeZone: 'Asia/Colombo' };
    const optionsTime = { hour: '2-digit', minute: '2-digit', hour12: true, timeZone: 'Asia/Colombo' };

    const formattedDate = now.toLocaleDateString('en-GB', optionsDate).replace(',', '').replace(' ', '/');
    const formattedTime = now.toLocaleTimeString('en-US', optionsTime).replace(/ /g, ''); // Fix time format

    return {
        date: `*_${formattedDate}_*`,
        time: `*_${formattedTime}_*`
    };
};

// DO NOT CHANGE NO MATTER WHAT: Function to determine the greeting based on time
const getGreeting = () => {
    const now = new Date().toLocaleString("en-US", { timeZone: "Asia/Colombo" });
    const hour = new Date(now).getHours(); // Extract hour in 24-hour format

    if (hour >= 5 && hour < 12) {
        return "Good morning ðŸŒ…";
    } else if (hour >= 12 && hour < 17) {
        return "Good afternoon â˜€ï¸";
    } else {
        return "Good evening ðŸŒ‡";
    }
};

