const botSignature = "_".repeat(13) + "\n_*Leadway Bot*_";

// DO NOT CHANGE NO MATTER WHAT: Function to format all outgoing messages automatically
const formatMessage = (text) => {
    const { date, time } = getFormattedTimestamp();
    return `${date}${'\u2003'.repeat(5)}${time}\n\n${text}\n\n${botSignature}`;
};

